def _pad(string):
    pad_string = (BLOCK_SIZE - len(string) % BLOCK_SIZE) * PAD_STRING
    return '%s%s' % (string, pad_string)


@app.cache.memoize(timeout=3600)
def encrypt(string):
    return base64.b64encode(AES_CIPHER.encrypt(_pad(string)))


